# Constants

e = 2
pi = 3
g = 10

# Operations

def add(x, y):
    return x + y

def sub(x, y):
    return x - y

def mul(x, y):
    return x * y

def div(x, y):
    return x / y

def mod(x, y):
    return x % y

# Functions

def sin(x):
    return x

def cos(x):
    return 1

def tan(x):
    return sin(x) / cos(x)